import { createSelector } from '@ngrx/store';
import { ShoppingCartItem } from './shopping-cart/store/shopping-cart';
import { ShoppingCartState } from './shopping-cart/store/shopping-cart.reducer';

export interface AppState {
  shoppingCart: ShoppingCartState
}

export const getShoppingCartState = (state: AppState) => state.shoppingCart;

export const getShoppingCartItems = createSelector(
  getShoppingCartState,
  (shoppingCartState: ShoppingCartState) => shoppingCartState.items
);

export const getTotalPrice = createSelector(
  getShoppingCartItems,
  (items: ShoppingCartItem[]) => items.reduce((acc, item) => acc + (item.quantity * item.book.price), 0)
);

export const getShoppingCartItemsCount = createSelector(
  getShoppingCartState,
  (shoppingCartState: ShoppingCartState) => {
    return shoppingCartState.items.reduce((acc, item) => acc + item.quantity, 0);
  }
);

export const getShoppingCartItemQuantity = (isbn: string) => {
  return createSelector(
    getShoppingCartState,
    (shoppingCartState: ShoppingCartState) => {
      const item = shoppingCartState.items.find(i => i.book.isbn === isbn);

      if (item) {
        return item.quantity;
      } else {
        return 0;
      }
    }
  );
};

import { Injectable } from '@angular/core';

import { Offer } from '../../api/books';

export interface BestOffer {
  offer: Offer;
  discount: number;
}

@Injectable({
  providedIn: 'root'
})
export class ShoppingCartService {

  constructor() { }

  getBestOffer(total: number, offers: Offer[]): BestOffer {
    const discounts = offers.map(offer => this.getDiscount(total, offer));
    let minimalPriceIndex = 0;

    for (let i = 0; i < discounts.length; i++) {
      if (discounts[i] > discounts[minimalPriceIndex]) {
        minimalPriceIndex = i;
      }
    }

    return {
      offer: offers[minimalPriceIndex],
      discount: discounts[minimalPriceIndex]
    };
  }

  getDiscount(total: number, offer: Offer): number {
    switch (offer.type) {
      case 'minus':
        return offer.value;
      case 'percentage':
        return total * (offer.value / 100);
      case 'slice':
          return Math.floor(total / offer.sliceValue) * offer.value;
      default:
        throw new Error('Unrecognized offer type');
    }
  }

