import { createSelector } from '@ngrx/store';
import { ShoppingCartState } from './shopping-cart/store/shopping-cart.reducer';

export interface AppState {
  shoppingCart: ShoppingCartState
}

export const getShoppingCartState = (state: AppState) => state.shoppingCart;

export const getShoppingCartItems = createSelector(
  getShoppingCartState,
  (shoppingCartState: ShoppingCartState) => shoppingCartState.items
);

export const getShoppingCartItemsCount = createSelector(
  getShoppingCartState,
  (shoppingCartState: ShoppingCartState) => {
    return shoppingCartState.items.reduce((acc, item) => acc + item.quantity, 0);
  }
);

export interface Book {
  isbn: string;
  title: string;
  price: number;
  cover: string;
  synopsis: string[];
}

import { Book } from '../../api/books';

export interface ShoppingCartItem {
  quantity: number;
  book: Book;
}

import { Action, createReducer, on } from '@ngrx/store';

import * as BooksSearchPageActions from '../../books/store/books-search.actions';
import { ShoppingCartItem } from './shopping-cart';

export interface ShoppingCartState {
  items: ShoppingCartItem[]
}

export const initialState: ShoppingCartState = {
  items: []
}

export const shoppingCartReducer = createReducer(
  initialState,
  on(BooksSearchPageActions.addToCart, (state, book) => {
    const index = state.items.findIndex(item => item.book.isbn === book.isbn);

    if (index >= 0) {
      return {
        ...state,
        items: [
          ...state.items.slice(0, index),
          {
            book,
            quantity: state.items[index].quantity + 1
          },
          ...state.items.slice(index + 1)
        ]
      };
    } else {
      return {
        ...state,
        items: [
          ...state.items,
          {
            book,
            quantity: 1
          }
        ]
      };
    }
})
);

export function reducer(state: ShoppingCartState | undefined, action: Action) {
  return shoppingCartReducer(state, action);
}

